angular
  .module("ui8Demo", [
    "ngResource",
    "ui.router",
    "ui8Demo.filters",
    "ui8Demo.services",
    "ui8Demo.directives",
    "ui8Demo.components",
  ])
  .config([
    "$locationProvider",
    function ($locationProvider) {
      $locationProvider.html5Mode(true);
    },
  ])
  .config([
    "$stateProvider",
    function ($stateProvider) {
      $stateProvider
        .state({
          name: "home",
          url: "^/",
          component: "home",
          resolve: {
            user: function (UserService) {
              return UserService.getUser(1);
            },
          },
        })
        .state({
          name: "about",
          url: "^/about",
          component: "about",
          resolve: {},
        });
    },
  ]);

angular.module("ui8Demo.filters", []);
angular.module("ui8Demo.services", []);
angular.module("ui8Demo.directives", []);
angular.module("ui8Demo.components", []);

angular.module("ui8Demo.filters").filter("firstName", [
  function () {
    return function (input) {
      return (input || "").split(" ")[0];
    };
  },
]);

angular.module("ui8Demo.services").service("UserService", [
  "$http",
  function ($http) {
    this.getUser = function (id) {
      return $http.get("/api/users/" + id).then(
        function (response) {
          return response.data;
        },
        function () {
          return null;
        }
      );
    };
  },
]);

angular.module("ui8Demo.components").component("about", {
  template: "<main><h1>About</h1><p>Something about us</p></main>",
  controller: function () {
    console.log("about");
  },
  bindings: {
    data: "<",
  },
});

angular.module("ui8Demo.components").component("home", {
  template:
    "<main><h1><friendly-greeting name='$ctrl.user.name' /></h1></main>",
  controller: function () {
    console.log("home");
  },
  bindings: {
    user: "<",
  },
});

angular.module("ui8Demo.components").component("friendlyGreeting", {
  template: "Hello, {{ $ctrl.name | firstName }}!",
  bindings: {
    name: "<",
  },
});

